import { useSelector } from "react-redux";
import { selectUserById } from "./usersSlice";
import { selectAllPosts } from "../posts/postsSlice";
import { Link, useParams } from "react-router-dom";
import { RootState } from "../../app/store";

const UserPage = () => {

    const { userId } = useParams();
    const user = useSelector((state: RootState) => selectUserById(state, Number(userId)));

    const postsForUser = useSelector((state: RootState) => {
        const allPosts = selectAllPosts(state);
        return allPosts.filter(post => post.userId === Number(userId));
    })

    const postTitles = postsForUser.map(post => (
        <li key={post.id}>
            <Link to={`/posts/${post.id}`} className='text-white' style={{'textDecoration': 'none'}}>{post.title}</Link>
        </li>
    ))

    return (
        <section className="w-100 h-100 d-flex flex-column justify-content-center align-items-center">
            <h2>Posts by {user.name}</h2>

            <ul>
                {postTitles}
            </ul>
        </section>
    );
}

export default UserPage;
