import { useDispatch } from "react-redux";
import { AppDispatch } from "../../app/store";
import { reactionAdded } from "./postsSlice";
// import { reactionAdded } from "./postsSlice";
import React from 'react'
import { Reactions } from "./postsSlice";
import { type } from "os";

type Props = {
    post: {
        id: string; 
        title: string; 
        content: string;
        userId: string;
        date: string;
        reactions: {
            thumbsUp: number;
            wow: number;
            heart: number;
            rocket: number;
            coffee: number;
        }
    }
}

const reactionEmoji = {
    thumbsUp: 'ðŸ‘',
    wow: 'ðŸ˜®',
    heart: 'â¤ï¸',
    rocket: 'ðŸš€',
    coffee: 'â˜•'
}

const ReactionButtons = ({post}: Props) => {

    const dispatch = useDispatch<AppDispatch>();

    // Object.entries(reactionEmoji) permette di mappare su tutto nell'oggetto
    const reactionButtons = Object.entries(reactionEmoji).map(([name, emoji] : [keyof Reactions, string]) => {
        return (
            <button
                key={name}
                type="button"
                className="reactionButton"
                onClick={() =>
                    dispatch(reactionAdded({ postId: post.id, reaction: name as keyof Reactions }))
                }
            >
                {emoji} {post.reactions[name]}
            </button>
        )
    })

    return (
        <div>
            {reactionButtons}
        </div>
    );
}

export default ReactionButtons
