import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { increaseCount, selectCount } from "../features/posts/postsSlice";
import { AppDispatch } from "../app/store";

const Header = () => {

    const dispatch = useDispatch<AppDispatch>();
    const count = useSelector(selectCount);

    return (
        <header className="w-100 d-flex flex-column justify-content-between align-items-center p-2">
            <h1 className="text-center">Redux Blog</h1>
            <nav className="w-100">
                <ul className="header-list w-100 d-flex justify-content-center align-items-center" style={{'gap': '50px'}}>
                    <li><Link to='/'>Home</Link></li>
                    <li><Link to='post'>Post</Link></li>
                    <li><Link to='user'>Users</Link></li>
                    <li>
                        <div className="count-box d-flex flex-column justify-content-center align-items-center" style={{'color': 'cyan'}}>
                            (This is a test, ignore this counter)
                            <button
                                type="button"
                                className="px-2"
                                // Funzione dispatch anonima
                                onClick={ () => {dispatch(increaseCount())} }
                            >
                                {count}
                            </button>
                        </div>
                    </li>
                </ul>
            </nav>
        </header>
    );
}

export default Header
