import { createSlice } from "@reduxjs/toolkit";
import { act } from "react-dom/test-utils";

// State iniziale (simile al reducer)
const initialState = [
    {
        id: '1', title: 'Learning Redux Toolkit', content: 'I\'ve heard good things.'
    },
    {
        id: '2', title: 'Slices...', content: 'The more i say slice, the more i want pizza.'
    }
]

// Export slice (oggetto che ha un nome, uno state iniziale, e i reducers che compieranno varie azioni)
const defaultSlice = createSlice({
    name: 'posts',
    initialState,
    reducers: {
        // Qui vanno le azioni reducer 
        // action: (state) => {state.default = newValue}
    }
});

// In futuro se cambia initialState non avremo più un array e si dovrà cambiare tutto
// Quindi si generalizza l'export

// Actions è la keyword per esportare le possibili azioni
export const {} = defaultSlice.actions;

// Export del reducer
export default defaultSlice.reducer;
